import CQN from "./cqn"

export class SELECT extends CQN.SELECT {
	static from (entity : string) : SELECT
	static distinct : SELECT
	from (entity : string) : this
	columns (...any) : this
	where (...expr : string) : this
	having (...expr : string) : this
	groupBy (...expr : string) : this
	orderBy (...expr : string) : this
	limit (rows : number, offset? : number) : this
}

export class INSERT extends CQN.INSERT {
	static into  (entity : string) : INSERT
	entries (...any) : this
	columns (...col: string) : this
	values (... val) : this
	rows (... row: []) : this
}

export class DELETE extends CQN.DELETE {
	static from  (entity : string) : DELETE
}

export class UPDATE extends CQN.UPDATE {
	static entity  (entity : string) : UPDATE
}

export class CREATE extends CQN.CREATE {
	static entity  (entity : string) : CREATE
}

export class DROP extends CQN.DROP {
	static entity  (entity : string) : DROP
}