module.exports = Object.assign (_new, {
    options: ['--modules'],
    help:`
# SYNOPSIS

    *cds new* [<dir>] [<options>]

    Creates a new project in the given directory


# OPTIONS
    *--modules [<modules>]*

        A comma-separated list of the modules to be created
        Valid modules are app, srv, db

# EXAMPLES

   *cds* new project
   *cds* new project --modules app,db
   *cds* new project --modules app

`})


function _new (args, options) {
    try{
        const generator = require('@sap/generator-cds')
        generator.new(args, options)
    } catch(e) { throw new Error(`
        This feature requires the module @sap/generator-cds to be installed.
        To do so, run "npm install -g @sap/generator-cds"
    `)}
}
