/**
 * FIXME: This is just for short-term compatibility to the API shipped to ServEC.
 * Should be removed asap when they adopted the target API.
 */
exports.connect = function _short_term_compat_connect (driver, clientOptions, poolOptions) {
  const {connect} = require('../index')
  const ql = require('@sap/cds-ql')
  connect.acquire = ql.connect.acquire
  connect.destroy = ql.connect.destroy
  connect.release = ql.connect.release
  return ql.connect.connect({
    driver: driver,
    connect: clientOptions,
    pool: poolOptions || {}
  })
}
